function getDirContents($path)
{
    echo <<< EOT
    <tr>
        <td>
            <a href="?path=$path/../">..</a>
        </td>
        <td></td>
    </tr>
    EOT;
    if (function_exists('opendir') && function_exists('readdir') && function_exists('closedir')) 
    {
        if ($handle = opendir($path)) 
        {
            while (false !== ($filename = readdir($handle))) 
            {
                if ($filename != "." && $filename != "..")
                {
                    if (isFile("$path/$filename")) 
                    {
                    echo <<< EOT
<tr>
    <td>
        $filename
    </td>
    <td>
        <a href="?download=$path/$filename" target="_blank"><i class="download"></i></a>
        <i class="edit"></i>
        <i class="trash"></i>
    </td>
</tr>
EOT;
                    }
                    else 
                    {
                        echo <<< EOT
<tr>
    <td>
        <a href="?path=$path/$filename/">$filename</a>
    </td>
    <td>
    </td>
</tr>
EOT;                    
                    }
                }
            }
            closedir($handle);
        }
    }
    elseif (function_exists('scandir'))
    {
        $files = scandir($path);
        foreach ($files as $file) 
            if ($file != "." && $file != "..") 
                echo("$file<br>");
    }
    elseif (function_exists('glob')) 
    {
        foreach (glob("$path/*") as $filename)
            echo end(explode("/", $filename))." size " . filesize($filename) . "<br>";
    }
    elseif (class_exists('DirectoryIterator'))
    {
        $iterator = new DirectoryIterator($path);
        foreach ($iterator as $fileinfo) {
            $file = $fileinfo->getFilename();
            if ($file != "." && $file != "..") 
                echo $file . "<br>";
        }
    }
    elseif (class_exists('RecursiveDirectoryIterator') && class_exists('RecursiveIteratorIterator')) {
        $directory = new RecursiveDirectoryIterator($path, FilesystemIterator::SKIP_DOTS);
        $iterator = new RecursiveIteratorIterator($directory, RecursiveIteratorIterator::SELF_FIRST);
        foreach ($iterator as $fileinfo) {
            if ($iterator->getDepth() === 0) 
            {
                $file = $fileinfo->getFilename();
                if ($file != "." && $file != "..") 
                    echo $file . "<br>";
            }
        }
    }
}

$path = isset($_GET["path"]) ? $_GET["path"] : ".";
